import styled from 'styled-components';

export const MenuBar = styled.div`
    display: flex;
`;

export const Name =  styled.h1`
    width: 100%;
    font-size: 48px;
    margin: 0;
    text-align: start;
    line-height: 1;
    font-weight: 700;
    font-family: "Poppins", sans-serif;
    @media (max-width: 968px) {
        padding: 0;
        text-align: center;
        font-size: 36px;
      }
`;

export const Statement = styled(Name)`
    font-size: 24px;
    margin-top: 20px;
    @media (max-width: 968px) {
        font-size: 18px;
      }
`;
