import styled from 'styled-components';

export const MenuBar = styled.div`
    display: inline-flex;
`;

export const Name =  styled.h1`
    width: 100%;
    margin: 0;
    padding: 20px;
    text-align: start;
    line-height: 1;
    color: #FFF;
    font-weight: 700;
    font-size: 36px;
    font-family: "Poppins", sans-serif;
    @media (max-width: 968px) {
        display: none;
      }
`;

export const IntroSection = styled.div`
    width: 100%;
`;

export const InputWrapper = styled.div`
  display: block;
  font-family: monospace;
  font-size: 125%;
  width: 50%;
  > input,
  > .placeholder {
    display: block;
    appearance: none;
    width: 100%;
    height: 100%;
    background-color: transparent;
    border: none;
  }
  > .placeholder {
    pointer-events: none;
    @keyframes typing {
      from { width: 0 }
      to { width: 100% }
    }
    animation: typing 1.5s steps(30, end) 1s infinite normal;
    white-space: nowrap;
    overflow: hidden;
    border-right: 2px solid;
    margin-right: 2px;
  }
  > input {
    &:focus,
    &:active {
      + .placeholder {
        display: none;
      }
    }
  }
`;
