import Navbar from '../navbar/navbar';
import {Name, MenuBar, IntroSection } from './style';
import laptop from './../../images/laptop.png';

import React, { useState, useEffect, useRef } from 'react';

function TypeWriter({ phrases, period = 2000 }) {
  const [text, setText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(300);
  const [isPaused, setIsPaused] = useState(false);
  const textRef = useRef(null);

  useEffect(() => {
    const handleType = () => {
        const i = loopNum % phrases.length;
        const fullTxt = phrases[i];
        let newText = '';

        if (isDeleting) {
          newText = fullTxt.substring(0, text.length - 1);
        } else {
          newText = fullTxt.substring(0, text.length + 1);
        }

        setText(newText);

        if (!isDeleting && newText === fullTxt) {
          setIsDeleting(true);
          setTimeout(() => setTypingSpeed(10000), 10000);
        } else if (isDeleting && newText === '') {
          setIsDeleting(false);
          setLoopNum(loopNum + 1);
          setTypingSpeed(150);
        }

        setTimeout(() => handleType(), typingSpeed);
      };


    handleType();
  }, [text, isDeleting, loopNum, typingSpeed, phrases, period, isPaused]);

  return (
    <h1>
      <a href="" className="typewriter">
        <span className="wrap" ref={textRef}>
          {text}
        </span>
      </a>
    </h1>
  );
}



export default function About() {
    return (
        <div>
            <MenuBar>
                <Name>Bisceanu Daniel</Name>
                <Navbar />
            </MenuBar>
            <IntroSection>
                <div>
                    <img src={laptop} />
                </div>
                <div>
                    <h1 style={{color: 'white'}}> Who am I?</h1>
                    <p style={{color: 'white'}}>As a highly motivated and eager Front-End Developer, I am excited to bring my passion for creating visually appealing and user-friendly websites to your team. With a strong background in HTML, CSS, and JavaScript, I have a solid foundation in web development and a keen eye for design. While I may not have professional experience in the field, I have always been passionate about web development and have been continuously honing my skills through various projects and online resources. I am a quick learner and thrive in fast-paced environments. I am confident that my passion, dedication, and willingness to continuously learn and adapt will make me a valuable asset to your team.</p>
                </div>
                <TypeWriter phrases={['Hi, I\'m Si.', 'I am Creative.', 'I Love Design.', 'I Love to Develop.']} speed={3000} />

             </IntroSection>
        </div>
        )
}
