import Navbar from '../navbar/navbar';
import {Name, MenuBar, IntroSection, InputWrapper,Input,Placeholder, Body } from './style';
import laptop from './../../images/laptop.png';

import React, { useState, useEffect, useRef } from 'react';

function TextGenerator(){
    useState( isRoating )
var TxtType = function(el, toRotate, period) {
    this.toRotate = toRotate;
    this.el = el;
    this.loopNum = 0;
    this.period = parseInt(period, 10) || 2000;
    this.txt = '';
    this.tick();
    this.isDeleting = false;
};

TxtType.prototype.tick = function() {
    var i = this.loopNum % this.toRotate.length;
    var fullTxt = this.toRotate[i];

    if (this.isDeleting) {
    this.txt = fullTxt.substring(0, this.txt.length - 1);
    } else {
    this.txt = fullTxt.substring(0, this.txt.length + 1);
    }

    this.el.innerHTML = '<span class="wrap">'+this.txt+'</span>';

    var that = this;
    var delta = 200 - Math.random() * 100;

    if (this.isDeleting) { delta /= 2; }

    if (!this.isDeleting && this.txt === fullTxt) {
    delta = this.period;
    this.isDeleting = true;
    } else if (this.isDeleting && this.txt === '') {
    this.isDeleting = false;
    this.loopNum++;
    delta = 500;
    }

    setTimeout(function() {
    that.tick();
    }, delta);
};

window.onload = function() {
    var elements = document.getElementsByClassName('typewrite');
    for (var i=0; i<elements.length; i++) {
        var toRotate = elements[i].getAttribute('data-type');
        var period = elements[i].getAttribute('data-period');
        if (toRotate) {
          new TxtType(elements[i], JSON.parse(toRotate), period);
        }
    }
    // INJECT CSS
    var css = document.createElement("style");
    css.type = "text/css";
    css.innerHTML = ".typewrite > .wrap { border-right: 0.08em solid #fff}";
    document.body.appendChild(css);
};

}

export default function About() {
    return (
        <div>
            <MenuBar>
                <Name>Bisceanu Daniel</Name>
                <Navbar />
            </MenuBar>
            <IntroSection>
                <div>
                    <img src={laptop} />
                </div>
                <div>
                    <h1 style={{color: 'white'}}> Who am I?</h1>
                    <p style={{color: 'white'}}>As a highly motivated and eager Front-End Developer, I am excited to bring my passion for creating visually appealing and user-friendly websites to your team. With a strong background in HTML, CSS, and JavaScript, I have a solid foundation in web development and a keen eye for design. While I may not have professional experience in the field, I have always been passionate about web development and have been continuously honing my skills through various projects and online resources. I am a quick learner and thrive in fast-paced environments. I am confident that my passion, dedication, and willingness to continuously learn and adapt will make me a valuable asset to your team.</p>
                </div>
                <h1>
                    <a href="" class="typewrite" data-period="2000" data-type='[ "Hi, I am Daniel.", "I am Creative.", "I Love Design.", "I Love to Develop." ]'>
                        <span class="wrap"></span>
                    </a>
                </h1>
                <Body>
      <InputWrapper>
        <Input aria-label="Ask us anything" />
        <Placeholder className="placeholder" />
      </InputWrapper>
    </Body>

             </IntroSection>
        </div>
        )
}
