import styled from 'styled-components';

export const MenuBar = styled.div`
    display: inline-flex;
`;

export const Name =  styled.h1`
    width: 100%;
    margin: 0;
    padding: 20px;
    text-align: start;
    line-height: 1;
    color: #FFF;
    font-weight: 700;
    font-size: 36px;
    font-family: "Poppins", sans-serif;
    @media (max-width: 968px) {
        display: none;
      }
`;

export const IntroSection = styled.div`
    display: inline-flex;
    width: 100%;
    & > div > img { 
        width: 400px;
    };
    & > div > h1 {
        position: absolute;
        margin-left: 160px;
        margin-top: -300px;
        font-size: 13px;
        transform: rotate(18deg);
    };
`;
