import styled from 'styled-components';

export const MenuBar = styled.div`
    display: inline-flex;
`;

export const Name =  styled.h1`
    width: 100%;
    margin: 0;
    padding: 20px;
    text-align: start;
    line-height: 1;
    color: #FFF;
    font-weight: 700;
    font-size: 36px;
    font-family: "Poppins", sans-serif;
    @media (max-width: 968px) {
        display: none;
      }
`;

export const HalfSection = styled.div`
     padding: 0;  
`

export const IntroSection = styled.div`
    display: inline-flex;
    max-width: 100%;
    margin: 0 400px;
    padding: 30px;
    background: rgba(0,0,0,.5);
    & > div > div > img { 
        width: 400px;
        filter: invert(1);
        opacity: 0.6;
    }
    & > div > div > div > h1 {
        position: absolute;
        margin-left: 200px;
        margin-top: -300px;
        font-size: 13px;
        transform: rotate(17deg);
        color: white;
    }
    @media( max-width: 968px) {
        margin: 0;
    }
`;

export const SkillBar = styled.div`
    padding: 30px;
    color: #FFF;
    & > ProgressBar > span {
        margin-left:5px;
    }
`;

export const SkillsSection = styled(IntroSection)`

`;
