import Navbar from '../navbar/navbar';
import {Name, MenuBar, IntroSection,Placeholder, Body, Input, Wrapper } from './style';
import laptop from './../../images/laptop.png';

import React, { useState, useEffect, useRef } from 'react';
const About = () => {
    const [txt, setTxt] = useState('');
    const txtRef = useRef();

    useEffect(() => {
      const words = [
        "Hi, I'm Si.",
        "I am Creative.",
        "I Love Design.",
        "I Love to Develop."
      ];
      let loopNum = 0;
      let isDeleting = false;
      let txt = '';
      let timeoutId;
      const tick = () => {
        const toRotate = words[loopNum];
        const delta = 200 - Math.random() * 100;
        if (isDeleting) {
          txt = toRotate.substring(0, txt.length - 1);
        } else {
          txt = toRotate.substring(0, txt.length + 1);
        }
        txtRef.current.innerHTML = `<span class="wrap">${txt}</span>`;
        if (!isDeleting && txt === toRotate) {
          delta = 1000;
          isDeleting = true;
        } else if (isDeleting && txt === '') {
          isDeleting = false;
          loopNum = (loopNum + 1) % words.length;
          delta = 500;
        }
        timeoutId = setTimeout(tick, delta);
      };
      tick();
      return () => clearTimeout(timeoutId);
    }, []);
    return (
        <div>
            <MenuBar>
                <Name>Bisceanu Daniel</Name>
                <Navbar />
            </MenuBar>
            <IntroSection>
                <div>
                    <img src={laptop} />
                </div>
                <div>
                    <h1 style={{color: 'white'}}> Who am I?</h1>
                    <p style={{color: 'white'}}>As a highly motivated and eager Front-End Developer, I am excited to bring my passion for creating visually appealing and user-friendly websites to your team. With a strong background in HTML, CSS, and JavaScript, I have a solid foundation in web development and a keen eye for design. While I may not have professional experience in the field, I have always been passionate about web development and have been continuously honing my skills through various projects and online resources. I am a quick learner and thrive in fast-paced environments. I am confident that my passion, dedication, and willingness to continuously learn and adapt will make me a valuable asset to your team.</p>
                </div>
                <h1>
                <a href="" class="typewrite" data-period="2000" data-type='[ "Hi, Im Si.", "I am Creative.", "I Love Design.", "I Love to Develop." ]'>
                    <span class="wrap"></span>
                </a>
                </h1>
             </IntroSection>
        </div>
        )
}

export default About;
