import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import Navbar from '../navbar/navbar';
import laptop from './../../images/laptop.png';

const MenuBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 1.5rem;
  background-color: #fff;
`;

const Name = styled.h1`
  margin: 0;
  font-size: 2rem;
`;

const IntroSection = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 3rem 0;
  background-color: #242526;
  color: #fff;

  & > div:first-child {
    flex: 0 0 50%;
  }

  & > div:last-child {
    flex: 0 0 40%;
  }
`;

const Placeholder = styled.span`
  display: block;
  pointer-events: none;
  white-space: nowrap;
  overflow: hidden;
`;

const Input = styled.input`
  display: block;
  appearance: none;
  width: 100%;
  background-color: transparent;
  border: none;
  color: #fff;
  font-size: 1.25rem;

  &:focus,
  &:active {
    + ${Placeholder} {
      display: none;
    }
  }
`;

const Wrapper = styled.div`
  display: block;
  font-family: monospace;
  font-size: 125%;
  width: 50%;

  > ${Input},
  > ${Placeholder} {
    display: block;
    width: 100%;
    height: 100%;
  }

  > ${Placeholder} {
    @keyframes typing {
      from { width: 0 }
      to { width: 100% }
    }

    display: block;
    animation: typing 3s steps(32), blink-caret 0.5s step-end infinite;
    white-space: nowrap;
    overflow: hidden;
    border-right: 0.08em solid #fff;
  }
`;

const About = () => {
  return (
    <div>
      <MenuBar>
        <Name>Bisceanu Daniel</Name>
        <Navbar />
      </MenuBar>
      <IntroSection>
        <div>
          <img src={laptop} />
        </div>
        <div>
          <h1 style={{color: 'white'}}> Who am I?</h1>
          <p style={{color: 'white'}}>As a highly motivated and eager Front-End Developer, I am excited to bring my passion for creating visually appealing and user-friendly websites to your team. With a strong background in HTML, CSS, and JavaScript, I have a solid foundation in web development and a keen eye for design. While I may not have professional experience in the field, I have always been passionate about web development and have been continuously honing my skills through various projects and online resources. I am a quick learner and thrive in fast-paced environments. I am confident that my passion, dedication, and willingness to continuously learn and adapt will make me a valuable asset to your team.</p>
        </div>
        <Wrapper>
          <Input type="text" aria-label="Ask us anything" />
          <Placeholder>How many roads must a man walk down before you call him a man?</Placeholder>
        </Wrapper>
      </IntroSection>
    </div>
  );
}

export default About;
